import React from 'react';

const textAreaListener = (ev) => {
  if (ev.target.value.includes('cebolla')) {
    alert('ODIO LA CEBOLLA');
  }
};

class OnionHaterRed extends React.Component {
  constructor(props) {
    super(props);
    this.isHating = false;
    this.handleOnion = this.handleOnion.bind(this);
  }
  handleOnion(event) {
    this.forceUpdate();
  }
  render() {
    return (
      <div className="onion_hater">
        <textarea className="onion_hater_text" onChange={textAreaListener} />
      </div>
    );
  }
}
export default OnionHaterRed;
