const fetchApiShows = () => {
  return fetch('http://api.tvmaze.com/search/shows?q=girls')
    .then((response) => response.json())
    .then((searchData) => {
      return searchData.show.map((show) => {
        return {
          id: show.id,
          name: show.name,
          img: show.image.medium,
          status: show.status,
          rating: show.rating.average,
        };
      });
    });
};

export default fetchApiShows;
