import React from 'react';
import showList from '../services/showList';
//import fetchApiShows from '../services/fetchApiShows';
import Header from './Header';
import ShowList from './ShowList';
import '../stylesheets/App.scss';

const placeImg = 'https://via.placeholder.com/210x295.png';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showInfo: showList.map((shows) => {
        return {
          id: shows.show.id,
          name: shows.show.name,
          status: shows.show.status,
          rating: shows.show.rating.average !== null ? shows.show.rating.average : 'No info',
          img: shows.show.image !== null ? shows.show.image.medium : placeImg,
        };
      }),
      isChecked: false,
      filterText: '',
    };
    this.searchText = this.searchText.bind(this);
  }

  // filterShows() {
  //   const shows = showList.map((shows) => {
  //     return {
  //       id: shows.show.id,
  //       name: shows.show.name,
  //       status: shows.show.status,
  //       rating: shows.show.rating.average,
  //       img: shows.show.image !== null ? shows.show.image.medium : placeImg,
  //     };
  //   });
  // }

  // fetchShows() {
  //   fetchApiShows().then((searchData) => {
  //     return searchData.map((shows) => {
  //       return this.setState({
  //         showInfo: {
  //           id: shows.show.id,
  //           name: shows.show.name,
  //           status: shows.show.status,
  //           rating: shows.show.rating.average,
  //           img: shows.show.image !== null ? shows.show.image.medium : placeImg,
  //         },
  //       });
  //     });
  //   });
  // }

  isChecked = (ev) => {
    console.log('is checked');
    this.setState({ isChecked: ev.currentTarget.checked });
  };

  searchText(text) {
    this.setState({
      filterText: text,
    });
  }

  render() {
    console.log(this.state);
    const filterShow = this.state.showInfo.filter((eachShow) => {
      return eachShow.name.toLowerCase().includes(this.state.filterText.toLowerCase());
    });
    // filterRunningShows() {
    //   const running = this.state.showInfo.filter((show) => show.status === 'Running');
    //   running.filtered = true;
    //   this.setState({ showInfo: this.state.showInfo });
    // }
    // console.log(this.state.showInfo);
    return (
      <div>
        <Header searchText={this.searchText} isChecked={this.isChecked} filterText={this.state.filterText} />
        <ShowList shows={filterShow} />
      </div>
    );
  }
}

export default App;
