import React from 'react';
import { fetchApiShows } from '../services/fetchApiShows';
import ComponenteMadre from './ComponenteMadre';
import '../stylesheets/App.scss';

console.log(fetchApiShows());

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = [];
  }

  fetchShows() {
    fetchApiShows().then((searchData) => {
      searchData.show.map((show) => {
        return this.setState({
          showData: { id: show.id, name: show.name, img: show.image.medium, status: show.status, rating: show.rating.average },
        });
      });
    });
  }
  render() {
    return (
      <div>
        <ComponenteMadre />
      </div>
    );
  }
}

export default App;
