const showInfo = {};
const placeImg = 'https://via.placeholder.com/126x177.png';

const fetchApiShows = () => {
  return fetch('http://api.tvmaze.com/search/shows?q=girls')
    .then((response) => response.json())
    .then((searchData) => {
      return searchData.map((shows) => {
        return this.setState({
          showInfo: {
            id: shows.show.id,
            name: shows.show.name,
            status: shows.show.status,
            rating: shows.show.rating.average,
            img: shows.show.image !== null ? shows.show.image.medium : placeImg,
          },
        });
      });
    });
};

console.log(showInfo);

export default fetchApiShows;
