import React from 'react';
import fetchApiShows from '../services/fetchApiShows';
import ComponenteMadre from './ComponenteMadre';
import '../stylesheets/App.scss';

const placeImg = 'https://via.placeholder.com/126x177.png';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showsInfo: {},
    };
  }

  fetchShows() {
    fetchApiShows().then((searchData) => {
      return searchData.map((shows) => {
        return this.setState((prevState) => {
          return {
            showsInfo: {
              ...prevState.showInfo,
              id: shows.show.id,
              name: shows.show.name,
              status: shows.show.status,
              rating: shows.show.rating.average,
              img: shows.show.image !== null ? shows.show.image.medium : placeImg,
            },
          };
        });
      });
    });
  }

  render() {
    console.log(this.state.showInfo);
    return (
      <div>
        <h2>Hola{this.state.id}</h2>
      </div>
    );
  }
}

export default App;
