import React from 'react';
import '../stylesheets/Filter.scss';

class Filter extends React.Component {
  handleChecked(ev) {
    this.props.ischecked(ev.currentTarget);
  }
  render() {
    console.log(this.props);

    return (
      <form className="filter__box">
        <label htmlFor="name">
          Filtra por nombre:
          <input id="name" type="text" />
        </label>
        <label>
          On air?
          <input type="checkbox" id="runing" onChange={this.handleChecked} />
        </label>
      </form>
    );
  }
}

export default Filter;
