import React from 'react';
import fetchApiShows from '../services/fetchApiShows';
import ComponenteMadre from './ComponenteMadre';
import '../stylesheets/App.scss';

console.log(fetchApiShows());

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showInfo: {},
    };
  }

  fetchShows() {
    fetchApiShows().then((searchData) => {
      return searchData.map((shows) => {
        return this.setState({
          showInfo: {
            id: shows.show.id,
            name: shows.show.name,
            status: shows.show.status,
            rating: shows.show.rating.average,
            img: shows.show.image !== null ? shows.show.image.medium : undefined,
          },
        });
      });
    });
  }

  render() {
    console.log(this.state.showInfo);
    return (
      <div>
        <ComponenteMadre />
      </div>
    );
  }
}

export default App;
